mapper.select:
SELECT COUNT(isNot)
FROM mro_anzhuangtiaoshi_Xiangmu_Service_Choose
WHERE choid = #{choid}
  AND isNot = 1
  AND xdid = #{id}

SELECT COUNT(isNotFinish)
FROM mro_anzhuangtiaoshi_Xiangmu_Service_Choose
WHERE serid = #{serid}
  AND isNotFinish = 1
  AND xdid = #{id}

select count(distinct czid)
from mro_anzhuangtiaoshi_Xiangmu_Service_Choose
where xdid = #{xdId,jdbcType=INTEGER}

select count(*)
from mro_anzhuangtiaoshi_Xiangmu_Service_Choose
where xdid = #{xdId,jdbcType=INTEGER}
  and serid = #{serid,jdbcType=INTEGER}
  and isNotFinish = 1

select count(*)
from mro_anzhuangtiaoshi_Xiangmu_Service_Choose
where xdid = #{xdId,jdbcType=INTEGER}
  and choid = #{choid,jdbcType=INTEGER}
  and isNot = 1
