mapper.select:
SELECT

FROM
alarm


STATUS = 0
AND station_id IN (
SELECT cz_id
    FROM mro_dianwuduan dwd,
         mro_xianduan xd,
         mro_chezhan cz
    WHERE dwd.did = xd.dwd_xd_id
      AND xd.xid = cz.xd_cz_id
      AND dwd.did = #{dwdid}
      AND xd.xid = #{xdid}
)

AND createtime BETWEEN ${starTime}
AND ${endTime}

SELECT

FROM
alarm

STATUS = 0
AND station_id =#{czid}

AND createtime BETWEEN ${starTime}
AND ${endTime}
