mapper.update:
update mro_dianwuduan


        dwd_state = #{dwdState},


        dwd_json = #{dwdJson}


where did = #{did}

update mro_xianduan


        xd_state = #{xdState},


        xd_json = #{xdJson}


where xid = #{xid}

update mro_chezhan


        cz_dmh_state = #{czdmhState},


        cz_dmh_json = #{czdmhJson}


where cid = #{cid}

update mro_chezhan


        cz_state = #{czState},


        cz_json = #{czJson}


where cid = #{cid}

update mro_dianwuduan
set dwd_state = #{dwdState},
    dwd_json  = #{dwdJson}
where did = #{did}

update mro_xianduan
set xd_state = #{xdState},
    xd_json  = #{xdJson}
where xid = #{xid}

update mro_chezhan
set cz_state = #{czState},
    cz_json  = #{czJson}
where cid = #{cid}

update mro_chezhan
set cz_dmh_state = #{czdmhState},
    cz_dmh_json  = #{czdmhJson}
where cid = #{cid}

mapper.select:
SELECT id, qdid, quDuanYunYing_name
FROM mro_quduan_base
WHERE cid = #{cid}

SELECT tlj.tlj_name,
       dwd.dwd_name,
       xd.xd_name,
       cz.cz_name,
       cz.cz_miaoshu
FROM mro_tieluju tlj,
     mro_dianwuduan dwd,
     mro_xianduan xd,
     mro_chezhan cz
WHERE tlj.tid = #{tid}
  AND dwd.did = #{did}
  AND xd.xid = #{xid}
  AND cz.cid = #{cid}

select tid, tlj_name
from mro_tieluju
where tid = #{tid, jdbcType=BIGINT}

select did, dwd_name
from mro_dianwuduan
where did = #{did, jdbcType=BIGINT}

select xid, xd_name
from mro_xianduan
where xid = #{xid, jdbcType=BIGINT}

select cid, cz_name
from mro_chezhan
where cid = #{cid, jdbcType=BIGINT}
