mapper.select:
select

from mro_equipment_wenti_return_visit_base
where renWuStartTime <=#{today}
and reWuEndTime >=#{today}
and implementState=1
and returnCycleType=1

select base.id, addUserid, renwuNumber, tljName, dwdName, xdName, returnUserid, returnedUserName, returnedUserNamePhone, returnCycleType, renWuStartTime, reWuEndTime, implementState, createTime, createName, updateTime, updateName,mc.name as returnUserName from mro_equipment_wenti_return_visit_base base, mro_customer mc
base.returnUserid=mc.id
and tljName like concat('%',#{tljName},'%')
and dwdName like concat('%',#{dwdName},'%')
and xdName like concat('%',#{xdName},'%')
and returnUserid =#{returnuserid}
and returnCycleType =#{returncycletype}
and renWuStartTime between #{statrTime} and #{endTime}
and implementState =#{implementstate}
